import React, { useState } from 'react';
import { Button, ButtonToolbar, Input } from 'rsuite';

const SearchBar = ({ data, onSearch }) => {
  const [query, setQuery] = useState('');

  const handleSearch = () => {
    const results = data.filter((item) =>
      Object.values(item).some((value) => value.toString().toLowerCase().includes(query.toLowerCase()))
    );
    onSearch(results);
  };

  return (
    <ButtonToolbar justify='start'>
      <Input
        style={{width:224}}
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search..."
      />
      <Button appearance='primary' onClick={handleSearch}>Search</Button>
    </ButtonToolbar>
  );
};

export default SearchBar;
